import React, { useState } from 'react';

const Task = ({ task }) => {
    const [isVisible, setIsVisible] = useState(false);

    // Stop the click event from propagating to parent elements
    const handleToggleVisibility = (e) => {
        e.stopPropagation();  // Prevents the event from reaching the parent's onClick
        setIsVisible(!isVisible);
    };

    return (
        <div onClick={handleToggleVisibility} style={{ cursor: 'pointer', marginBottom: '10px' }}>
            <div>
                {task.title}
                {task.sub_items && task.sub_items.length > 0 && (
                    <button onClick={handleToggleVisibility} style={{ marginLeft: '10px' }}>
                        {isVisible ? 'Hide' : 'Show'}
                    </button>
                )}
            </div>
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => (
                        // Render each sub-item using the Task component recursively
                        <Task key={subItem.id} task={subItem} />
                    ))}
                </div>
            )}
        </div>
    );
};
