import React, { useState } from 'react';

const Task = ({ task }) => {
    const [isVisible, setIsVisible] = useState(false);

    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };

    return (
        <div>
            <div style={{ cursor: 'pointer' }} onClick={toggleVisibility}>
                {task.title}
                {task.sub_items && task.sub_items.length > 0 && (
                    <button onClick={toggleVisibility}>
                        {isVisible ? 'Hide Sub-tasks' : 'Show Sub-tasks'}
                    </button>
                )}
            </div>
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => <Task key={subItem.id} task={subItem} />)}
                </div>
            )}
        </div>
    );
};

export default Task;
