import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const NewTaskPage = () => {
    const [nextId, setNextId] = useState();
    useEffect(() => {
        // Fetch nextId only when component mounts or based on specific conditions
        const getNextTaskId = () => {
            return fetch('http://127.0.0.1:5000/next-id', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
            })
            .then(response => response.json())
            .then(data => setNextId(data.next_id));
        };

        if (!nextId) { // Fetch nextId if not already set
            getNextTaskId();
        }
    }, []);
    const tempId = 1000000
  // State to hold the main task and its subtasks
  const [tasks, setTasks] = useState([
    { id: nextId, title: '', subtasks: [{ id: nextId + 1, title: '', subsubtasks: [{ id: tempId, title: '' }] }] },
  ]);
    const [numSub, setNumSub] = useState(1);    

  const getCurrentUserId = () => {
    const token = localStorage.getItem('token');
    if (!token) return null;

    try {
      const decodedToken = jwtDecode(token);
      return decodedToken.sub.identity; 
    } catch (error) {
      console.error('Error decoding token:', error);
      return null;
    }
  };



  const handleSubmit = async (event) => {
    event.preventDefault();
    // Assuming you have a way to get the current user's ID
    const userId = getCurrentUserId(); // You need to implement this based on your auth system

    let currentId = nextId; // Initialize currentId with the base ID provided

// Flatten tasks to include parent_id and user_id
const flattenedTasks = tasks.flatMap(task => [
    { ...task, parent_id: null, user_id: userId },
    ...task.subtasks.flatMap(subtask => [
      { ...subtask, parent_id: task.id, user_id: userId },
      ...subtask.subsubtasks.map(subsubtask => ({ ...subsubtask, parent_id: subtask.id, user_id: userId }))
    ])
  ]);
      console.log('Flattened tasks:', flattenedTasks);
    // try {
    //   const response = await fetch('http://127.0.0.1:5000/new-list', {
    //     method: 'POST',
    //     headers: {
    //       'Content-Type': 'application/json',
    //       // Include auth token if needed
    //     },
    //     body: JSON.stringify(flattenedTasks),
    //   });

    //   if (!response.ok) throw new Error('Network response was not ok.');
    //   // Handle success
    //   console.log('Tasks successfully submitted:', await response.json());
    //   window.location.href = '/'; // Redirect to the dashboard
    // } catch (error) {
    //   console.error('Error submitting tasks:', error);
    // }
};
    const increaseNumSub = () => {
        setNumSub(numSub + 1);
    }


  // Function to add a new subtask or sub-subtask
  const addSubTask = (taskId, subtaskId = null) => {
    setTasks(tasks.map(task => {
      if (task.id === taskId) {
        if (subtaskId === null) {
          const newSubtaskId = taskId + numSub;
          task.subtasks.push({ id: newSubtaskId, parent_id: taskId, title: '', subsubtasks: [] });
          increaseNumSub();
        } else {
          task.subtasks.forEach(subtask => {
            if (subtask.id === subtaskId) {
              tempId++;
              subtask.subsubtasks.push({ id: tempId, parent_id: subtaskId, title: '' });
            }
          });
        }
      }
      return task;
    }));
  };


  // Function to handle task title change
  const handleTaskChange = (taskId, subtaskId, subsubtaskId, value) => {
    setTasks(tasks.map(task => {
      if (task.id === taskId) {
        if (subtaskId === null) {
          task.title = value;
        } else {
          task.subtasks.forEach(subtask => {
            if (subtask.id === subtaskId) {
              if (subsubtaskId === null) {
                subtask.title = value;
              } else {
                subtask.subsubtasks.forEach(subsubtask => {
                  if (subsubtask.id === subsubtaskId) {
                    subsubtask.title = value;
                  }
                });
              }
            }
          });
        }
      }
      return task;
    }));
  };

  return (
    <>
      <nav>
        <Link to="/">Home</Link>
      </nav>

      <div className="new-task-page">
        <h2>Add New Task</h2>
        <form onSubmit={handleSubmit}>
          {tasks.map((task, taskIndex) => (
            <div key={task.id}>
              <input
                required
                type="text"
                name={`title-${task.id}`}
                placeholder="Title"
                value={task.title}
                onChange={(e) => handleTaskChange(task.id, null, null, e.target.value)}
              />
              <button type="button" onClick={() => addSubTask(task.id)}>Add Subtask</button>

              {task.subtasks.map((subtask) => (
                <div key={subtask.id}>
                  <input
                    required
                    type="text"
                    name={`title-${subtask.id}`}
                    placeholder="Subtask Title"
                    value={subtask.title}
                    onChange={(e) => handleTaskChange(task.id, subtask.id, null, e.target.value)}
                  />
                  <button type="button" onClick={() => addSubTask(task.id, subtask.id)}>Add Sub-Subtask</button>

                  {subtask.subsubtasks.map((subsubtask) => (
                <input
                    key={subsubtask.id}
                    required
                    type="text"
                    name={`title-${subsubtask.id}`}
                    placeholder="Sub-Subtask Title"
                    value={subsubtask.title}
                    onChange={(e) => handleTaskChange(task.id, subtask.id, subsubtask.id, e.target.value)}
                />
                ))}
                </div>
              ))}
            </div>
          ))}
          <button type="submit">Add Task</button>
        </form>
        </div>
    </>
    );
};   

export default NewTaskPage;
