import React, { useState } from 'react';

const Task = ({ task, onEdit, onDelete }) => {
    const [isVisible, setIsVisible] = useState(true); // Assuming you want tasks visible initially
    const [isEditing, setIsEditing] = useState(false);
    const [editTitle, setEditTitle] = useState(task.title);

    const handleToggleVisibility = (e) => {
        e.stopPropagation(); // This should prevent the event from bubbling up
        setIsVisible(!isVisible);
    };

    const handleEdit = (e) => {
        e.stopPropagation(); // Stop propagation here as well
        setIsEditing(true);
    };

    const handleDelete = (e) => {
        e.stopPropagation(); // And here
        onDelete(task.id);
    };

    const handleSave = (e) => {
        e.stopPropagation(); // Ensure propagation is stopped when saving
        onEdit(task.id, editTitle);
        setIsEditing(false);
    };

    const handleChange = (e) => {
        setEditTitle(e.target.value);
    };

    return (
        <div style={{ cursor: 'pointer', marginBottom: '10px' }}>
            <div onClick={handleToggleVisibility}>
                {isEditing ? (
                    <div>
                        <input type="text" value={editTitle} onChange={handleChange} />
                        <button onClick={handleSave}>Save</button>
                    </div>
                ) : (
                    <div>
                        {task.title}
                        <button onClick={handleEdit} style={{ marginLeft: '10px' }}>Edit</button>
                        <button onClick={handleDelete} style={{ marginLeft: '10px' }}>Delete</button>
                    </div>
                )}
            </div>
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => (
                        <Task key={subItem.id} task={subItem} onEdit={onEdit} onDelete={onDelete} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default Task;
