import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Task from '../components/Task';

const HomePage = () => {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
            const token = localStorage.getItem('token'); // Assuming token is stored here
            try {
                const response = await fetch('http://127.0.0.1:5000/lists', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });
                if (!response.ok) {
                    window.location.href = '/login'; // Redirect to login page if not authorized
                    //throw new Error('Network response was not ok');
                }
                const data = await response.json();
                setTasks(data);
            } catch (error) {
                console.error("Error fetching tasks:", error);
            }
        };

        fetchTasks();
    }, []);

    const onEdit = (id, title) => {
        fetch('http://127.0.0.1:5000/edit-task', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id, title }),
        })
        .then(response => response.json())
        .then(data => {
            console.log('Success:', data);
            // Optionally, update local state to reflect the edited task
        })
        .catch(error => console.error('Error:', error));
    };

    const onDelete = (id) => {
        fetch(`http://127.0.0.1:5000/delete-task/${id}`, {
            method: 'DELETE',
        })
        .then(response => response.json())
        .then(data => {
            console.log('Delete successful:', data);
            // Optionally, update local state to remove the deleted task
        })
        .catch(error => console.error('Error:', error));
    };

    const renderTasks = (tasks) => {
        return tasks.map(task => (
            <Task key={task.id} task={task} onEdit={onEdit} onDelete={onDelete} />
        ));
    };



    return (
        <>
            <nav>
                <Link to="/new-task">Add New Task</Link>
            </nav>
            <div className="home-page">
                <h2>Lists</h2>
                <div className="tasks">
                    {renderTasks(tasks)}
                </div>
            </div>
        </>
    );
};

export default HomePage;
