import React from 'react';
import Task from './Task'; 

const List = ({ list, onEdit, onDelete }) => {
    const DeleteList = (id) => {
        const token = localStorage.getItem('token');
        fetch(`http://127.0.0.1:5000/delete-list/${id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => response.json())
        .then(data => {
            console.log('Delete successful:', data);
            // Update local state to remove the deleted list
            const remainingLists = list.filter(list => list.id !== id);
            setLists(remainingLists);
        })
        .catch(error => console.error('Error:', error));
    };
  return (
    <div>
      <h2>{list.name}</h2> 
        <button onClick={() => DeleteList(list.id)}>Delete List</button>
      <div>
        {list.tasks.map(task => (
          <Task key={task.id} task={task} onEdit={onEdit} onDelete={onDelete} />
        ))}
      </div>
    </div>
  );
};

export default List;
