import React, { useState } from 'react';

const Task = ({ task, onEdit, onDelete }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editTitle, setEditTitle] = useState(task.title);

    const handleToggleVisibility = (e) => {
        e.stopPropagation();
        setIsVisible(!isVisible);
    };  

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleDelete = () => {
        onDelete(task.id);
    };

    const handleSave = () => {
        onEdit(task.id, editTitle);
        setIsEditing(false);
    };

    const handleChange = (e) => {
        setEditTitle(e.target.value);
    };

    return (
        <div onClick={handleToggleVisibility} style={{ cursor: 'pointer', marginBottom: '10px' }}>
            {/* Component structure remains the same */}
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => (
                        // Pass the visibility state down but do not allow sub-items to toggle it directly
                        <Task key={subItem.id} task={subItem} onEdit={onEdit} onDelete={onDelete} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default Task;
