import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Corrected import for jwt-decode

const NewTaskPage = () => {
    // if token is not present, redirect to login page
    if (!localStorage.getItem('token')) {
        window.location.href = '/login';
    }
  const [nextId, setNextId] = useState(10000000); 
  const [isLoading, setIsLoading] = useState(true); 
  const [numSub, setNumSub] = useState(1);
  const [numSubSub, setNumSubSub] = useState(0);

  useEffect(() => {
    const getNextTaskId = async () => {
        setIsLoading(true);
      try {
        const response = await fetch('http://127.0.0.1:5000/next-id', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
        if (response.ok) {
        const data = await response.json();
        setNextId(data.next_id);
        setIsLoading(false); // Update loading state based on data fetch
        } else {
          throw new Error('Network response was not ok.');
        }
      } catch (error) {
        console.error('Failed to fetch nextId:', error);
      } finally {
        setIsLoading(false); // Correctly updating state, not a hook call
      }
    };

    getNextTaskId();
  }, []);

  useEffect(() => {
    console.log('Next ID:', nextId);
  }, [nextId]);



    let tempId = 100000000;
  // State to hold the main task and its subtasks
  const [tasks, setTasks] = useState([
    { id: nextId, title: '', subtasks: [{ id: nextId + 1, title: '', subsubtasks: [{ id: tempId, title: '' }] }] },
  ]);

  const getCurrentUserId = () => {
    const token = localStorage.getItem('token');
    if (!token) return null;
    try {
      const decodedToken = jwtDecode(token);
      return decodedToken.sub.identity; 
    } catch (error) {
      console.error('Error decoding token:', error);
      return null;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Assuming you have a way to get the current user's ID
    const userId = getCurrentUserId(); // You need to implement this based on your auth system

    let currentId = nextId; // Initialize currentId with the base ID provided

function flattenTasks(tasks, userId, parentId = null) {
    let flattened = [];
    tasks.forEach(task => {
        const mainTask = { ...task, parent_id: null, user_id: userId };
        flattened.push(mainTask);
        if (task.subtasks && task.subtasks.length > 0) {
            // Increment the currentId for subtasks to ensure uniqueness
            currentId += 1;
            const subTasksFlattened = flattenTasks(task.subtasks, userId, currentId);
            flattened = [...flattened, ...subTasksFlattened];
        }
    });
    return flattened;
}

// Usage
const flattenedTasks = flattenTasks(tasks, userId);


      console.log('Flattened tasks:', flattenedTasks);
