import React from 'react';
import Task from './Task'; 
import { Droppable } from 'react-beautiful-dnd';

const List = ({ list, onEdit, onDelete }) => {
    const deleteList = (id) => {
        const token = localStorage.getItem('token');
        fetch(`http://127.0.0.1:5000/delete-list/${id}`, {
            method: 'DELETE',
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
        .then(response => response.json())
        .then(data => {
            console.log('Delete successful:', data);
            // Update local state to remove the deleted list
            window.location.href = '/'; // Refresh the page
        })
        .catch(error => console.error('Error:', error));
    };
  return (
    <Droppable droppableId={String(list.id)}>
            {(provided) => (
                <div {...provided.droppableProps} ref={provided.innerRef}>
                    <h2>{list.name}</h2>
                    <button onClick={() => deleteList(list.id)}>Delete List</button>
                    <div>
                        {list.tasks.map((task, index) => (
                            <Task key={task.id} task={task} index={index} onEdit={onEdit} onDelete={onDelete} />
                        ))}
                        {provided.placeholder}
                    </div>
                </div>
            )}
        </Droppable>
  );
};

export default List;
