import React, { useState } from 'react';

const Task = ({ task, onEdit, onDelete }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editTitle, setEditTitle] = useState(task.title);
    const [selectedListId, setSelectedListId] = useState(task.list_id);

    const handleToggleVisibility = (e) => {
        e.stopPropagation();
        setIsVisible(!isVisible);
    };  

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleDelete = () => {
        onDelete(task.id);
    };

    const handleSave = () => {
        onEdit(task.id, editTitle);
        setIsEditing(false);
    };

    const handleChange = (e) => {
        setEditTitle(e.target.value);
    };

    const onMoveTask = (taskId, newListId) => {
        const token = localStorage.getItem('token');
        fetch(`http://127.0.0.1:5000/move-task/${taskId}`, {
            method: 'PUT', // or POST, depending on your backend implementation
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ new_list_id: newListId }),
        })
        .then(response => response.json())
        .then(data => {
            console.log('Task moved:', data);
            // Update the tasks state to reflect the move on the frontend
            const newTasks = tasks.map(task => {
                if (task.id === taskId) {
                    return { ...task, list_id: newListId };
                }
                return task;
            });
            setTasks(newTasks);
        })
        .catch(error => console.error('Error:', error));
    };


    const handleMoveTask = (e) => {
        const newListId = e.target.value;
        setSelectedListId(newListId);
        onMoveTask(task.id, newListId);
    };

    return (
        <div onClick={handleToggleVisibility} style={{ cursor: 'pointer', marginBottom: '10px' }}>
            {isEditing ? (
                <div>
                    <input type="text" value={editTitle} onChange={handleChange} />
                    <button onClick={handleSave}>Save</button>
                </div>
            ) : (
                <div>
                    {task.title}
                    <select value={selectedListId} onChange={handleMoveTask}>
                        {lists.map(list => (
                            <option key={list.id} value={list.id}>
                                {list.name}
                            </option>
                        ))}
                    </select>
                    <button onClick={handleEdit} style={{ marginLeft: '10px' }}>Edit</button>
                    <button onClick={handleDelete} style={{ marginLeft: '10px' }}>Delete</button>
                    {task.sub_items && task.sub_items.length > 0 && (
                        <button onClick={handleToggleVisibility} style={{ marginLeft: '10px' }}>
                            {isVisible ? 'Hide' : 'Show'}
                        </button>
                    )}
                </div>
            )}
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => (
                        <Task key={subItem.id} task={subItem} onEdit={onEdit} onDelete={onDelete} />
                    ))}
                </div>
            )}
        </div>
    );
};

export default Task;
