import React, { useState } from 'react';
import { Draggable } from 'react-beautiful-dnd';

const Task = ({ task, index, onEdit, onDelete }) => {
    const [isVisible, setIsVisible] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editTitle, setEditTitle] = useState(task.title);

    const handleToggleVisibility = (e) => {
        e.stopPropagation();
        setIsVisible(!isVisible);
    };  

    const handleEdit = () => {
        setIsEditing(true);
    };

    const handleDelete = () => {
        onDelete(task.id);
    };

    const handleSave = () => {
        onEdit(task.id, editTitle);
        setIsEditing(false);
    };

    const handleChange = (e) => {
        setEditTitle(e.target.value);
    };

    return (
        <Draggable draggableId={String(task.id)} index={index}>
            {(provided) => (
                <div ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>

        <div onClick={handleToggleVisibility} style={{ cursor: 'pointer', marginBottom: '10px' }}>
            {isEditing ? (
                <div>
                    <input type="text" value={editTitle} onChange={handleChange} />
                    <button onClick={handleSave}>Save</button>
                </div>
            ) : (
                <div>
                    {task.title}
                    <button onClick={handleEdit} style={{ marginLeft: '10px' }}>Edit</button>
                    <button onClick={handleDelete} style={{ marginLeft: '10px' }}>Delete</button>
                    {task.sub_items && task.sub_items.length > 0 && (
                        <button onClick={handleToggleVisibility} style={{ marginLeft: '10px' }}>
                            {isVisible ? 'Hide' : 'Show'}
                        </button>
                    )}
                </div>
            )}
            {isVisible && task.sub_items && task.sub_items.length > 0 && (
                <div className="sub-tasks" style={{ marginLeft: '20px' }}>
                    {task.sub_items.map(subItem => (
                        <Task key={subItem.id} task={subItem} onEdit={onEdit} onDelete={onDelete} />
                    ))}
                </div>
            )}
        </div>
        </div>

        )}
    </Draggable>
    );
};

export default Task;
